mapper.update:
UPDATE address
SET state       = 0,
    modify_time = #{modifyTime}
WHERE id = #{id}
      AND user_id = #{userId}

UPDATE address
SET type        = 0,
    modify_time = #{modifyTime}
WHERE user_id = #{userId}
      AND state = 1

UPDATE address
SET type = 1
WHERE id = #{id}
      AND user_id = #{userId}
      AND state = 1

UPDATE address
SET address_description = #{addressDescription},
    phone_number        = #{phoneNumber},
    modify_time         = #{modifyTime}
WHERE id = #{id}
      AND user_id = #{userId}
      AND state = 1

mapper.select:
SELECT *
FROM address

    id = #{id}

        AND state = #{state}


        AND user_id = #{userId}
