mapper.select:
SELECT *
FROM commodity
WHERE type = #{type}
      AND id != #{id}
      AND state = 1

mapper.update:
UPDATE commodity
SET state       = 0,
    modifier    = #{modifier},
    modify_time = #{modifyTime}
WHERE id = #{id}

UPDATE commodity
SET inventory   = #{inventory},
    modify_time = #{modifyTime}
WHERE id = #{id}
